mapper.select:
select 'true' as QUERYID,

from building_purpose
where bp_id = #{bpId,jdbcType=INTEGER}

mapper.update:
update building_purpose
set purpose_name = #{purposeName,jdbcType=VARCHAR},
  gmt_create = #{gmtCreate,jdbcType=TIMESTAMP},
  gmt_modifile = #{gmtModifile,jdbcType=TIMESTAMP},
  del_id = #{delId,jdbcType=INTEGER}
where bp_id = #{bpId,jdbcType=INTEGER}
