mapper.select:
select 'true' as QUERYID,

from dwell_cause
where dc_id = #{dcId,jdbcType=INTEGER}

mapper.update:
update dwell_cause
set dwell_cause_name = #{dwellCauseName,jdbcType=VARCHAR},
  gmt_create = #{gmtCreate,jdbcType=TIMESTAMP},
  gmt_modifile = #{gmtModifile,jdbcType=TIMESTAMP},
  del_id = #{delId,jdbcType=INTEGER}
where dc_id = #{dcId,jdbcType=INTEGER}
