mapper.select:
select 'true' as QUERYID,

from dwell_way
where dw_id = #{dwId,jdbcType=INTEGER}

mapper.update:
update dwell_way
set dwell_way_name = #{dwellWayName,jdbcType=VARCHAR},
  gmt_create = #{gmtCreate,jdbcType=TIMESTAMP},
  gmt_modifile = #{gmtModifile,jdbcType=TIMESTAMP},
  del_id = #{delId,jdbcType=INTEGER}
where dw_id = #{dwId,jdbcType=INTEGER}
