mapper.select:
select 'true' as QUERYID,

from house_category
where hc_id = #{hcId,jdbcType=INTEGER}

mapper.update:
update house_category
set house_category_name = #{houseCategoryName,jdbcType=VARCHAR},
  gmt_create = #{gmtCreate,jdbcType=TIMESTAMP},
  gmt_modifile = #{gmtModifile,jdbcType=TIMESTAMP},
  del_id = #{delId,jdbcType=INTEGER}
where hc_id = #{hcId,jdbcType=INTEGER}
