mapper.select:
select 'true' as QUERYID,

from house_type
where ht_id = #{htId,jdbcType=INTEGER}

mapper.update:
update house_type
set house_type_name = #{houseTypeName,jdbcType=VARCHAR},
  gmt_create = #{gmtCreate,jdbcType=TIMESTAMP},
  gmt_modifile = #{gmtModifile,jdbcType=TIMESTAMP},
  del_id = #{delId,jdbcType=INTEGER}
where ht_id = #{htId,jdbcType=INTEGER}
