set political_name = #{politicalName,jdbcType=VARCHAR},
  creation_time = #{creationTime,jdbcType=TIMESTAMP},
  revision_time = #{revisionTime,jdbcType=TIMESTAMP},
  del = #{del,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}
